mapper.update:
update buyer_order0
set
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select ID
from buyer_order0
where buyer_uid = #{buyerUid,jdbcType=VARCHAR}

  and status = #{status,jdbcType=INTEGER}

ORDER BY id desc
LIMIT #{offSet,jdbcType=INTEGER},#{rows,jdbcType=INTEGER}
